$.fn.i18n =function(options){
    this.each(function() {
        var $this = $(this);
        $.getJSON("js/lang/i18n_"+options+".json", function(data) {
            var key = $this.data('lang');
            var val = data[key];
            $this.html(val);
        });
      });
    return this;
}